import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { FileSpreadsheet, Upload, X, Images, Plus, ArrowRight } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import type { InventoryItem } from "@shared/schema";

interface ExcelUploadProps {
  mode: 'edit';
  onJobCreated: (jobId: string) => void;
  onProcessingComplete: () => void;
}

export function ExcelUpload({ mode, onJobCreated, onProcessingComplete }: ExcelUploadProps) {
  const [selectedExcelFile, setSelectedExcelFile] = useState<File | null>(null);
  const [selectedImages, setSelectedImages] = useState<File[]>([]);
  const [uploading, setUploading] = useState(false);
  const [step, setStep] = useState<'load-excel' | 'add-screenshots'>('load-excel');
  const excelInputRef = useRef<HTMLInputElement>(null);
  const imagesInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  // Check if inventory has items to determine current step
  const { data: inventoryItems = [] } = useQuery<InventoryItem[]>({
    queryKey: ["/api/inventory"],
  });

  const handleExcelSelect = (files: FileList | null) => {
    if (!files || files.length === 0) return;

    const file = files[0];
    if (!file.name.toLowerCase().endsWith('.xlsx') && !file.name.toLowerCase().endsWith('.xls')) {
      toast({
        title: "–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç —Ñ–∞–π–ª–∞",
        description: "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª (.xlsx –∏–ª–∏ .xls)",
        variant: "destructive",
      });
      return;
    }

    setSelectedExcelFile(file);
  };

  const handleImagesSelect = (files: FileList | null) => {
    if (!files) return;

    const imageFiles = Array.from(files).filter(file => 
      file.type.startsWith('image/')
    );

    if (imageFiles.length === 0) {
      toast({
        title: "–ù–µ–≤–µ—Ä–Ω—ã–µ —Ñ–∞–π–ª—ã",
        description: "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —Ç–æ–ª—å–∫–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è",
        variant: "destructive",
      });
      return;
    }

    setSelectedImages(prev => [...prev, ...imageFiles]);
  };

  const removeImage = (index: number) => {
    setSelectedImages(prev => prev.filter((_, i) => i !== index));
  };

  const removeExcelFile = () => {
    setSelectedExcelFile(null);
  };

  // Auto-detect current step based on inventory
  const currentStep = inventoryItems.length > 0 ? 'add-screenshots' : step;

  const loadExcelFile = async () => {
    if (!selectedExcelFile) {
      toast({
        title: "–§–∞–π–ª –Ω–µ –≤—ã–±—Ä–∞–Ω",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª",
        variant: "destructive",
      });
      return;
    }

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('excelFile', selectedExcelFile);

      const response = await fetch('/api/load-excel', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const data = await response.json();
      onJobCreated(data.jobId);

      // Reset form and move to next step
      setSelectedExcelFile(null);
      setStep('add-screenshots');

      toast({
        title: "–ó–∞–≥—Ä—É–∑–∫–∞ –Ω–∞—á–∞—Ç–∞",
        description: "–ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ Excel —Ñ–∞–π–ª–∞ –≤ –±–∞–∑—É...",
      });
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å Excel —Ñ–∞–π–ª",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  const addScreenshots = async () => {
    if (selectedImages.length === 0) {
      toast({
        title: "–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –Ω–µ –≤—ã–±—Ä–∞–Ω—ã",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–Ω–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ",
        variant: "destructive",
      });
      return;
    }

    setUploading(true);
    try {
      const formData = new FormData();

      selectedImages.forEach(file => {
        formData.append('images', file);
      });

      const response = await fetch('/api/add-screenshots', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const data = await response.json();
      onJobCreated(data.jobId);

      // Reset images
      setSelectedImages([]);

      toast({
        title: "–û–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–∞—á–∞—Ç–∞",
        description: `–î–æ–±–∞–≤–ª—è–µ–º ${selectedImages.length} –Ω–æ–≤—ã—Ö —Å–∫—Ä–∏–Ω—à–æ—Ç–æ–≤ –≤ –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å...`,
      });
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∏ –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  return (
    <Card className="bg-gray-800 border-gray-700">
      <CardHeader>
        <CardTitle className="flex items-center text-white">
          <FileSpreadsheet className="text-green-500 mr-3 text-xl" />
          {currentStep === 'load-excel' ? '–ó–∞–≥—Ä—É–∑–∫–∞ Excel —Ñ–∞–π–ª–∞' : '–î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Å–∫—Ä–∏–Ω—à–æ—Ç–æ–≤'}
        </CardTitle>
        <div className="flex items-center space-x-4 mt-2">
          <div className={`flex items-center ${currentStep === 'load-excel' ? 'text-blue-400' : 'text-green-400'}`}>
            <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center mr-2 ${
              currentStep === 'load-excel' ? 'border-blue-400 bg-blue-400' : 'border-green-400 bg-green-400'
            }`}>
