import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { FileSpreadsheet, Upload, X, Split, Download } from "lucide-react";
import * as XLSX from "xlsx";

interface ExcelSplitProps {
  onJobCreated?: (jobId: string) => void;
  onProcessingComplete?: () => void;
}

export function ExcelSplit({ onJobCreated, onProcessingComplete }: ExcelSplitProps) {
  const [selectedExcelFile, setSelectedExcelFile] = useState<File | null>(null);
  const [processing, setProcessing] = useState(false);
  const [threshold, setThreshold] = useState(11);
  const [splitResults, setSplitResults] = useState<{
    lowPriceData: any[];
    highPriceData: any[];
    lowCount: number;
    highCount: number;
  } | null>(null);
  const excelInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleExcelSelect = (files: FileList | null) => {
    if (!files || files.length === 0) return;

    const file = files[0];
    if (!file.name.toLowerCase().endsWith('.xlsx') && !file.name.toLowerCase().endsWith('.xls')) {
      toast({
        title: "–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç —Ñ–∞–π–ª–∞",
        description: "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª (.xlsx –∏–ª–∏ .xls)",
        variant: "destructive",
      });
      return;
    }

    setSelectedExcelFile(file);
    setSplitResults(null);
  };

  const removeExcelFile = () => {
    setSelectedExcelFile(null);
    setSplitResults(null);
  };

  const splitExcel = async () => {
    if (!selectedExcelFile) {
      toast({
        title: "–§–∞–π–ª –Ω–µ –≤—ã–±—Ä–∞–Ω",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª –¥–ª—è —Ä–∞–∑–¥–µ–ª–µ–Ω–∏—è",
        variant: "destructive",
      });
      return;
    }

    setProcessing(true);
    try {
      const arrayBuffer = await selectedExcelFile.arrayBuffer();
      const workbook = XLSX.read(arrayBuffer, { type: "array" });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const data = XLSX.utils.sheet_to_json(sheet);

      const lowPriceData: any[] = [];
      const highPriceData: any[] = [];

      data.forEach((row: any) => {
        const pricesStr = row['–¶–µ–Ω–∞ –ø—Ä–æ–¥–∞–∂–∏'] || '';
        const prices = pricesStr.split(',').map((p: string) => parseFloat(p.trim()) || 0).filter((p: number) => !isNaN(p));

        if (prices.length === 0) {
          lowPriceData.push(row); // –ï—Å–ª–∏ –Ω–µ—Ç —Ü–µ–Ω, –≤ –Ω–∏–∑–∫–∏–µ
          return;
        }

        const lowCount = prices.filter((p: number) => p < threshold).length;
        const highCount = prices.filter((p: number) => p >= threshold).length;

        // –ë–æ–ª—å—à–∏–Ω—Å—Ç–≤–æ —Ü–µ–Ω –æ–ø—Ä–µ–¥–µ–ª—è–µ—Ç —Ñ–∞–π–ª
        if (highCount > lowCount) {
          highPriceData.push(row);
        } else {
          lowPriceData.push(row);
        }
      });

      setSplitResults({
        lowPriceData,
        highPriceData,
        lowCount: lowPriceData.length,
        highCount: highPriceData.length,
      });

      if (onJobCreated) onJobCreated(Date.now().toString());
      if (onProcessingComplete) onProcessingComplete();

      toast({
        title: "–†–∞–∑–¥–µ–ª–µ–Ω–∏–µ –∑–∞–≤–µ—Ä—à–µ–Ω–æ",
        description: `–§–∞–π–ª —Ä–∞–∑–¥–µ–ª–µ–Ω: ${lowPriceData.length} –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –¥–æ ${threshold-1} –ø–ª–∞—Ç–∏–Ω—ã, ${highPriceData.length} –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –æ—Ç ${threshold} –ø–ª–∞—Ç–∏–Ω—ã`,
      });
    } catch (error) {
      console.error(error);
      toast({
        title: "–û—à–∏–±–∫–∞ —Ä–∞–∑–¥–µ–ª–µ–Ω–∏—è",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Ä–∞–∑–¥–µ–ª–∏—Ç—å Excel —Ñ–∞–π–ª",
        variant: "destructive",
      });
    } finally {
      setProcessing(false);
    }
  };

  const downloadFile = (fileType: 'low' | 'high') => {
    if (!splitResults) return;

    try {
      const data = fileType === 'low' ? splitResults.lowPriceData : splitResults.highPriceData;
      const workbook = XLSX.utils.book_new();
      const worksheet = XLSX.utils.json_to_sheet(data);
      XLSX.utils.book_append_sheet(workbook, worksheet, fileType === 'low' ? 'Low Price' : 'High Price');

      XLSX.writeFile(workbook, fileType === 'low' ? 'warframe_inventory_low_price.xlsx' : 'warframe_inventory_high_price.xlsx');

      toast({
        title: "–§–∞–π–ª —Å–∫–∞—á–∞–Ω",
        description: `${fileType === 'low' ? '–ù–∏–∑–∫–∏–µ —Ü–µ–Ω—ã' : '–í—ã—Å–æ–∫–∏–µ —Ü–µ–Ω—ã'} —Ñ–∞–π–ª —É—Å–ø–µ—à–Ω–æ —Å–∫–∞—á–∞–Ω`,
      });
    } catch (error) {
      console.error(error);
      toast({
        title: "–û—à–∏–±–∫–∞ —Å–∫–∞—á–∏–≤–∞–Ω–∏—è",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –∏–ª–∏ —Å–∫–∞—á–∞—Ç—å —Ñ–∞–π–ª",
        variant: "destructive",
      });
    }
  };

  return (
    <Card className="bg-gray-800 border-gray-700">
      <CardHeader>
        <CardTitle className="flex items-center text-white">
          <Split className="text-teal-500 mr-3 text-xl" />
          –†–∞–∑–¥–µ–ª–µ–Ω–∏–µ –ø–æ —Ü–µ–Ω–µ
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div>
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-sm font-medium text-gray-300">–ü–æ—Ä–æ–≥ —Ä–∞–∑–¥–µ–ª–µ–Ω–∏—è (–ø–ª–∞—Ç–∏–Ω–∞)</h3>
            <div className="flex items-center space-x-2">
              <input
                type="number"
                value={threshold}
                onChange={(e) => setThreshold(parseInt(e.target.value) || 11)}
                min="1"
                max="100"
                className="w-16 px-2 py-1 text-sm bg-gray-700 border border-gray-600 rounded text-white"
                disabled={processing}
              />
              <Button
                variant="outline"
                size="sm"
                onClick={() => excelInputRef.current?.click()}
                className="border-gray-600 hover:bg-gray-700"
                disabled={processing}
              >
                <Upload className="mr-2 h-4 w-4" />
                –í—ã–±—Ä–∞—Ç—å Excel
              </Button>
            </div>
          </div>

          {selectedExcelFile ? (
            <div className="flex items-center justify-between bg-gray-700 rounded-lg p-3">
              <div className="flex items-center">
                <FileSpreadsheet className="text-teal-500 mr-3 h-4 w-4" />
                <span className="text-sm text-gray-300">{selectedExcelFile.name}</span>
              </div>
              <Button
                variant="ghost"
                size="icon"
                onClick={removeExcelFile}
                className="text-red-400 hover:text-red-300"
              >
                <X className="h-4 w-4" />
              </Button>
            </div>
          ) : (
            <div className="border-2 border-dashed border-gray-600 rounded-lg p-4 text-center">
              <p className="text-sm text-gray-400">Excel —Ñ–∞–π–ª –Ω–µ –≤—ã–±—Ä–∞–Ω</p>
            </div>
          )}

          <input
            ref={excelInputRef}
            type="file"
            accept=".xlsx,.xls"
            className="hidden"
            onChange={(e) => handleExcelSelect(e.target.files)}
          />
        </div>

        <Button
          onClick={splitExcel}
          disabled={!selectedExcelFile || processing}
          className="w-full bg-teal-600 hover:bg-teal-700 disabled:bg-gray-600"
        >
          {processing ? "–†–∞–∑–¥–µ–ª—è–µ–º —Ñ–∞–π–ª..." : "–†–∞–∑–¥–µ–ª–∏—Ç—å –ø–æ —Ü–µ–Ω–µ"}
        </Button>

        {splitResults && (
          <div className="space-y-3 border-t border-gray-600 pt-4">
            <h4 className="text-sm font-medium text-gray-300">–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞–∑–¥–µ–ª–µ–Ω–∏—è:</h4>

            <div className="grid grid-cols-2 gap-3">
              <Button
                onClick={() => downloadFile('low')}
                variant="outline"
                className="flex items-center justify-center bg-green-900/20 border-green-600 text-green-400 hover:bg-green-900/30"
              >
                <Download className="mr-2 h-4 w-4" />
                –î–æ {threshold-1} –ø–ª–∞—Ç–∏–Ω—ã
                <span className="ml-1 text-xs">({splitResults.lowCount})</span>
              </Button>

              <Button
                onClick={() => downloadFile('high')}
                variant="outline"
                className="flex items-center justify-center bg-red-900/20 border-red-600 text-red-400 hover:bg-red-900/30"
              >
                <Download className="mr-2 h-4 w-4" />
                –û—Ç {threshold} –ø–ª–∞—Ç–∏–Ω—ã
                <span className="ml-1 text-xs">({splitResults.highCount})</span>
              </Button>
            </div>
          </div>
        )}

        <div className="text-xs text-gray-400 bg-gray-700 rounded-lg p-3">
          <p className="font-medium mb-1">–ö–∞–∫ —ç—Ç–æ —Ä–∞–±–æ—Ç–∞–µ—Ç:</p>
          <ul className="space-y-1 text-gray-400">
            <li>‚Ä¢ –£—Å—Ç–∞–Ω–æ–≤–∏—Ç–µ –ø–æ—Ä–æ–≥ —Ä–∞–∑–¥–µ–ª–µ–Ω–∏—è (–ø–æ —É–º–æ–ª—á–∞–Ω–∏—é 11 –ø–ª–∞—Ç–∏–Ω—ã)</li>
            <li>‚Ä¢ –ó–∞–≥—Ä—É–∑–∏—Ç–µ Excel —Ñ–∞–π–ª —Å —Ü–µ–Ω–∞–º–∏</li>
            <li>‚Ä¢ –°–∏—Å—Ç–µ–º–∞ –ø—Ä–æ–≤–µ—Ä–∏—Ç –±–æ–ª—å—à–∏–Ω—Å—Ç–≤–æ —Ü–µ–Ω —É –∫–∞–∂–¥–æ–≥–æ –ø—Ä–µ–¥–º–µ—Ç–∞</li>
            <li>‚Ä¢ –ï—Å–ª–∏ –±–æ–ª—å—à–µ —Ü–µ–Ω –±–æ–ª—å—à–µ –∏–ª–∏ —Ä–∞–≤–Ω–æ –ø–æ—Ä–æ–≥—É ‚Üí –≤ high_price —Ñ–∞–π–ª</li>
            <li>‚Ä¢ –ï—Å–ª–∏ –±–æ–ª—å—à–µ —Ü–µ–Ω –º–µ–Ω—å—à–µ –ø–æ—Ä–æ–≥–∞ ‚Üí –≤ low_price —Ñ–∞–π–ª</li>
          </ul>
        </div>
      </CardContent>
    </Card>
  );
}

