import { useState, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { FileSpreadsheet, Upload, X, RefreshCw } from "lucide-react";

interface PriceUpdateProps {
  onJobCreated: (jobId: string) => void;
  onProcessingComplete: () => void;
}

export function PriceUpdate({ onJobCreated, onProcessingComplete }: PriceUpdateProps) {
  const [selectedExcelFile, setSelectedExcelFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const excelInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const handleExcelSelect = (files: FileList | null) => {
    if (!files || files.length === 0) return;

    const file = files[0];
    if (!file.name.toLowerCase().endsWith('.xlsx') && !file.name.toLowerCase().endsWith('.xls')) {
      toast({
        title: "–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç —Ñ–∞–π–ª–∞",
        description: "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª (.xlsx –∏–ª–∏ .xls)",
        variant: "destructive",
      });
      return;
    }

    setSelectedExcelFile(file);
  };

  const removeExcelFile = () => {
    setSelectedExcelFile(null);
  };

  const updatePrices = async () => {
    if (!selectedExcelFile) {
      toast({
        title: "–§–∞–π–ª –Ω–µ –≤—ã–±—Ä–∞–Ω",
        description: "–í—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª —Å –Ω–∞–∑–≤–∞–Ω–∏—è–º–∏ –ø—Ä–µ–¥–º–µ—Ç–æ–≤",
        variant: "destructive",
      });
      return;
    }

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('excelFile', selectedExcelFile);

      const response = await fetch('/api/update-prices', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Price update failed');
      }

      const data = await response.json();
      onJobCreated(data.jobId);

      // Reset form
      setSelectedExcelFile(null);

      toast({
        title: "–û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –Ω–∞—á–∞—Ç–æ",
        description: "–û–±–Ω–æ–≤–ª—è–µ–º —Ü–µ–Ω—ã –ø—Ä–µ–¥–º–µ—Ç–æ–≤ —á–µ—Ä–µ–∑ Warframe Market API...",
      });
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å —Ü–µ–Ω—ã",
        variant: "destructive",
      });
    } finally {
      setUploading(false);
    }
  };

  return (
    <Card className="bg-gray-800 border-gray-700">
      <CardHeader>
        <CardTitle className="flex items-center text-white">
          <RefreshCw className="text-orange-500 mr-3 text-xl" />
          –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Ü–µ–Ω
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Excel File Upload */}
        <div>
          <div className="flex items-center justify-between mb-3">
            <h3 className="text-sm font-medium text-gray-300">–í—ã–±–µ—Ä–∏—Ç–µ Excel —Ñ–∞–π–ª</h3>
            <Button
              variant="outline"
              size="sm"
              onClick={() => excelInputRef.current?.click()}
              className="border-gray-600 hover:bg-gray-700"
              disabled={uploading}
            >
              <Upload className="mr-2 h-4 w-4" />
              –í—ã–±—Ä–∞—Ç—å Excel
            </Button>
          </div>

          {selectedExcelFile ? (
            <div className="flex items-center justify-between bg-gray-700 rounded-lg p-3">
              <div className="flex items-center">
                <FileSpreadsheet className="text-orange-500 mr-3 h-4 w-4" />
                <span className="text-sm text-gray-300">{selectedExcelFile.name}</span>
              </div>
              <Button
                variant="ghost"
                size="icon"
                onClick={removeExcelFile}
